document.addEventListener('DOMContentLoaded',function(){const pages=document.querySelectorAll('.page');const navDots=document.querySelectorAll('.nav-dot');const scrollIndicator=document.querySelector('.scroll-indicator');let currentPage=0;window.scrollTo(0,0);function updateActivePage(index){currentPage=index;navDots.forEach((dot,i)=>{if(i===index){dot.classList.add('active');dot.setAttribute('aria-current','true');}else{dot.classList.remove('active');dot.removeAttribute('aria-current');}});if(scrollIndicator){scrollIndicator.style.display=index===0?'block':'none';}}navDots.forEach((dot,index)=>{dot.addEventListener('click',()=>{navigateToPage(index);});});function navigateToPage(index){if(index>=0&&index<pages.length){pages[index].scrollIntoView({behavior:'smooth',block:'start'});updateActivePage(index);}}document.addEventListener('keydown',(e)=>{switch(e.key){case 'ArrowDown':case 'PageDown':e.preventDefault();if(currentPage<pages.length-1){navigateToPage(currentPage+1);}break;case 'ArrowUp':case 'PageUp':e.preventDefault();if(currentPage>0){navigateToPage(currentPage-1);}break;case 'Home':e.preventDefault();navigateToPage(0);break;case 'End':e.preventDefault();navigateToPage(pages.length-1);break;}});let scrolling=false;document.addEventListener('wheel',(e)=>{e.preventDefault();if(scrolling)return;scrolling=true;if(e.deltaY>0&&currentPage<pages.length-1){navigateToPage(currentPage+1);}else if(e.deltaY<0&&currentPage>0){navigateToPage(currentPage-1);}setTimeout(()=>{scrolling=false;},2000);},{passive:false});let touchStartY=0;let touchEndY=0;document.addEventListener('touchstart',(e)=>{touchStartY=e.touches[0].clientY;},{passive:true});document.addEventListener('touchend',(e)=>{touchEndY=e.changedTouches[0].clientY;handleSwipe();},{passive:true});function handleSwipe(){const swipeThreshold=50;const swipeDistance=touchStartY-touchEndY;if(Math.abs(swipeDistance)>swipeThreshold){if(swipeDistance>0&&currentPage<pages.length-1){navigateToPage(currentPage+1);}else if(swipeDistance<0&&currentPage>0){navigateToPage(currentPage-1);}}}let resizeTimeout;window.addEventListener('resize',()=>{clearTimeout(resizeTimeout);resizeTimeout=setTimeout(()=>{navigateToPage(currentPage);},250);});updateActivePage(0);document.body.classList.add('loaded');});